parameters.jsonBody:
={
  "contents": [{
    "parts": [{
      "text": "Conduct a comprehensive deep research analysis on the topic: '{{ $json.research_topic }}'. Depth level: {{ $json.depth_level }}/5. Please provide:\n\n1. Executive Summary\n2. Key findings and insights\n3. Current trends and developments\n4. Statistical data and evidence\n5. Expert opinions and citations\n6. Future implications\n7. Research gaps identified\n\nFormat the response in structured markdown with clear sections and subsections. Include credible sources and references where possible."
    }]
  }],
  "generationConfig": {
    "temperature": 0.2,
    "topP": 0.8,
    "maxOutputTokens": 8192
  }
}

={
  "model": "llama-3.1-sonar-large-128k-online",
  "messages": [
    {
      "role": "system",
      "content": "You are a research analyst tasked with verifying, fact-checking, and enhancing research content with the most current information available online. Provide additional context, recent developments, and cross-reference findings."
    },
    {
      "role": "user",
      "content": "Please enhance and fact-check this research on '{{ $json.research_topic }}' with current online information:\n\n{{ $json.gemini_research }}\n\nPlease:\n1. Verify key claims and statistics\n2. Add recent developments (2024-2025)\n3. Provide additional sources and references\n4. Highlight any contradictions or updates\n5. Add current market data or trends\n\nMaintain the original structure but enhance with verified online information."
    }
  ],
  "temperature": 0.3,
  "max_tokens": 4000,
  "search_domain_filter": ["google.com", "scholar.google.com", "pubmed.ncbi.nlm.nih.gov", "arxiv.org"]
}

assignments.value:
